import {useState} from "react";

export const ItemForm = ({onAddTask}) => {
    const [name,setName] = useState("");
    const addTask = (e) => {
        e.preventDefault();
        const task = {
            name,
            done: false,
        };
        onAddTask(task);
        setName("");
    }
    return (
        <div className="d-flex justify-content-center">
            {/*todo app title*/}
            <form className="d-flex my-2 bg-primary w-50  justify-content-between rounded-2">
                <input type="text" className="form-control" placeholder="Add Task" value={name} onChange={(e)=>setName(e.target.value)} />
                <button className="btn bg-primary text-white fw-bold fs-4" onClick={addTask}>Add</button>
            </form>
        </div>
    );
}
