import { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import {ItemList} from "./components/ItemList";

function App() {
  const [tasks, setTasks] = useState([]);

  const onAddTask = (task) => {
    setTasks([...tasks, task]);
  };

  return (
    <div>
     <ItemList onAddTask={onAddTask} tasks={tasks}/>
    </div>
  );
}

export default App;
