import {useState} from "react";

export const ItemForm = ({onAddTask}) => {
    const [name,setName] = useState("");

    const addTask = (e) => {
        e.preventDefault();
        const task = {
            name,
            done: false,
        };
        onAddTask(task);
        setName("");
    }
    return (
        <div className="d-flex flex-column align-content-center ">
            <h1 className="text-center" data-testid="app-title">Todo Tasks App</h1>
            <form className="d-flex my-2 bg-primary w-75  mx-auto justify-content-between rounded-2">
                <input data-testid="task-name" type="text" className="form-control" placeholder="Add Task" value={name} onChange={(e)=>setName(e.target.value)} />
                <button data-testid="task-add-btn" className="btn bg-primary text-white fw-bold fs-4" onClick={()=>onAddTask(task)}>Add</button>
            </form>
        </div>
    );
}
